721:             function update(now) {
722:                 const elapsed = now - start;
723:                 const progress = Math.min(elapsed / duration, 1);
724:                 
725:                 // Ease out cubic
726:                 const eased = 1 - Math.pow(1 - progress, 3);
727:                 const current = Math.floor(startNum + (num - startNum) * eased);
