/**
 * Adnify Website - Main JavaScript
 * Contributors & Testimonials Animations
 */

document.addEventListener('DOMContentLoaded', () => {

    // --- INTERSECTION OBSERVER FOR SCROLL ANIMATIONS ---
    const observerOptions = {
        root: null,
        rootMargin: '50px 0px',
        threshold: 0.05
    };

    const animationObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('visible');

                // For contributor items, add staggered animation
                if (entry.target.classList.contains('contributors-grid')) {
                    const items = entry.target.querySelectorAll('.contributor-item');
                    items.forEach((item, index) => {
                        setTimeout(() => {
                            item.classList.add('visible');
                        }, index * 80);
                    });
                }

                // Unobserve after animation triggered
                animationObserver.unobserve(entry.target);
            }
        });
    }, observerOptions);

    // Elements to observe for scroll animations
    const animatedElements = document.querySelectorAll(`
        .section-title, 
        .section-subtitle, 
        .contributor-card, 
        .contributors-grid, 
        .contribute-cta
    `);

    animatedElements.forEach(el => {
        animationObserver.observe(el);
    });

    // Fallback: If elements are already in viewport on load, trigger animation
    setTimeout(() => {
        animatedElements.forEach(el => {
            const rect = el.getBoundingClientRect();
            const inViewport = rect.top < window.innerHeight && rect.bottom > 0;
            if (inViewport && !el.classList.contains('visible')) {
                el.classList.add('visible');

                // Handle contributor items
                if (el.classList.contains('contributors-grid')) {
                    const items = el.querySelectorAll('.contributor-item');
                    items.forEach((item, index) => {
                        setTimeout(() => {
                            item.classList.add('visible');
                        }, index * 80);
                    });
                }
            }
        });
    }, 100);

    // --- TESTIMONIALS AUTO-SCROLL DUPLICATION ---
    const testimonialsTrack = document.querySelector('.testimonials-track');
    if (testimonialsTrack) {
        // Clone testimonial cards for infinite scroll effect
        const cards = testimonialsTrack.querySelectorAll('.testimonial-card');
        cards.forEach(card => {
            const clone = card.cloneNode(true);
            testimonialsTrack.appendChild(clone);
        });
    }

    // --- TESTIMONIALS INDICATOR INTERACTION ---
    const indicatorDots = document.querySelectorAll('.indicator-dot');
    let currentIndicator = 0;

    function updateIndicator() {
        indicatorDots.forEach((dot, index) => {
            dot.classList.toggle('active', index === currentIndicator);
        });
        currentIndicator = (currentIndicator + 1) % indicatorDots.length;
    }

    // Auto-update indicator every 13 seconds (synced with scroll animation)
    if (indicatorDots.length > 0) {
        setInterval(updateIndicator, 13000);

        // Click to change indicator
        indicatorDots.forEach((dot, index) => {
            dot.addEventListener('click', () => {
                currentIndicator = index;
                indicatorDots.forEach((d, i) => {
                    d.classList.toggle('active', i === index);
                });
            });
        });
    }

    // --- PARALLAX EFFECT FOR CONTRIBUTOR GLOW ---
    const contributorGlow = document.querySelector('.contributor-glow');
    const contributorCard = document.querySelector('.contributor-card.contributor-lead');

    if (contributorCard && contributorGlow) {
        contributorCard.addEventListener('mousemove', (e) => {
            const rect = contributorCard.getBoundingClientRect();
            const x = (e.clientX - rect.left) / rect.width - 0.5;
            const y = (e.clientY - rect.top) / rect.height - 0.5;

            contributorGlow.style.transform = `translate(calc(-50% + ${x * 30}px), calc(-50% + ${y * 30}px))`;
        });

        contributorCard.addEventListener('mouseleave', () => {
            contributorGlow.style.transform = 'translate(-50%, -50%)';
        });
    }

});


